CREATE TABLE IF NOT EXISTS DOCUMENT_HIST (HIST_ID BIGINT NOT NULL AUTO_INCREMENT,ROW_START TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,ROW_END TIMESTAMP NULL DEFAULT NULL,ID BIGINT NOT NULL,DESCRIPTION VARCHAR(255),DISPLAYNAME VARCHAR(255),DOCDATE DATE,PRIMARY KEY (HIST_ID));
CREATE TABLE IF NOT EXISTS DOCFILE_HIST (HIST_ID BIGINT NOT NULL AUTO_INCREMENT,ROW_START TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,ROW_END TIMESTAMP NULL DEFAULT NULL,ID BIGINT NOT NULL,COMMENT VARCHAR(255),CONFIRM TINYINT(1) DEFAULT 0,USERNAME VARCHAR(255),DOCUMENT_ID BIGINT NOT NULL,PRIMARY KEY (HIST_ID));
